import Vue from 'vue'
import Router from 'vue-router'
import HelloContainer from "./components/HelloWorld.vue"
// src/router.js 配置组件访问路由
// 1.引入自定义的组件
import HomeContainer from "./components/tabbar/HomeContainer";
import NewsContainer from "./components/news/NewsContainer";
import Vip from "./components/tabbar/Vip";
import Cart from "./components/tabbar/Cart";
import Search from "./components/tabbar/Search";
import NewsDetail from "./components/news/NewsDetailContainer";
import GoodList from "./components/goods/GoodList";
import GoodsInfo from "./components/goods/GoodsInfo";


Vue.use(Router)

// 指定该组件的访问路径
export default new Router({
  routes: [
    {path:'/',redirect:"/home"},
    {path:"/home",component:HomeContainer},
    {path:"/home/newslist",component:NewsContainer},
    {path:"/home/goodslist",component:GoodList},
    {path:"/home/goodsinfo/:id",component:GoodsInfo,name:"goodsinfo"},
    {path:"/vip",component:Vip},
    {path:"/cart",component:Cart},
    {path:"/search",component:Search},
    {path:"/newsdetail/:id",component:NewsDetail},
  ],linkActiveClass:"mui-active"
})
