import { Toast } from "mint-ui";
import header from "../subcomponents/header";
import footer from "../subcomponents/footer";
import aside from "../subcomponents/Aside";
export default {
  data: function data() {
    return {
      lid: this.$route.params.lid,
      details: {},
      imagelist: []
    };
  },
  methods: {
    goSub: function goSub() {
      if (this.num <= 1) {
        return;
      }

      this.num--;
    },
    goAdd: function goAdd() {
      if (this.num >= 99) {
        return;
      }

      this.num++;
    },
    // 获取当前商品数据
    getDetails: function getDetails() {
      var _this = this;

      // 发送请求获取数据
      this.$http.get("product/details?lid=" + this.lid).then(function (result) {
        // 保存在info数据对象中
        _this.details = result.body; // console.log(this.details);
      });
    },
    getImageList: function getImageList() {
      var _this2 = this;

      var url = "product/goodspic?lid=" + this.lid;
      this.$http.get(url).then(function (result) {
        _this2.imagelist = result.body;
        console.log(_this2.imagelist);
      });
    }
  },
  created: function created() {
    this.getImageList();
    this.getDetails();
    this.getImageList();
  },
  components: {
    'header-box': header,
    //注册子组件
    'footer-box': footer,
    'aside-box': aside
  }
};
