import Vue from 'vue'
import Router from 'vue-router'

// 1.引入自定义的组件
import Index from "./components/index/Index";



Vue.use(Router)

// 指定该组件的访问路径
export default new Router({
  routes: [
    {path:'/',redirect:"/index"},
    {path:"/index",component:Index},

  ],linkActiveClass:"mui-active"
})
